import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Container, Box, Typography, TextField, Button, 
  FormControl, InputLabel, Select, MenuItem,
  Slider, Grid, Alert
} from '@mui/material';
import axios from 'axios';

function UserPreferences() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    major: 'Select a Major',
    minor: 'None',
    specialization: 'None',
    interests: '',
    year: '1',
    coreCount: 4,
    electiveCount: 2,
    degreeType: 'Please Select an Option',
    studentStatus: 'Please Select an Option'
  });

  const [majors, setMajors] = useState([]);
  const [totalCourses, setTotalCourses] = useState(6);
  const [error, setError] = useState('');

  // Fetch majors from backend
  useEffect(() => {
    const fetchMajors = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/majors');
        setMajors(['Select a Major', ...response.data]);
      } catch (error) {
        console.error('Error fetching majors:', error);
        setMajors(['Select a Major', 'Computer Science', 'Engineering', 'Business']);
      }
    };

    fetchMajors();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSliderChange = (name) => (e, newValue) => {
    setFormData(prev => ({ ...prev, [name]: newValue }));

    if (name === 'coreCount' || name === 'electiveCount') {
      const core = name === 'coreCount' ? newValue : formData.coreCount;
      const elective = name === 'electiveCount' ? newValue : formData.electiveCount;
      setTotalCourses(core + elective);
    }
  };

  const handleSubmit = async () => {
    if (formData.major === 'Select a Major') {
      setError('Please select a major to continue.');
      return;
    }

    try {
      // Send user preferences to backend
      await axios.post('http://127.0.0.1:8000/extract_courses', { major: formData.major });

      // Navigate based on student status
      if (formData.studentStatus === 'Yes') {
        navigate('/course-list');
      } else if (formData.studentStatus === 'No') {
        navigate('/chatbot');
      }
    } catch (error) {
      console.error('Error submitting preferences:', error);
      setError('Failed to submit preferences. Please try again.');
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Your Academic Profile
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Your Name"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Major</InputLabel>
              <Select
                name="major"
                value={formData.major}
                onChange={handleChange}
                label="Major"
              >
                {majors.map(major => (
                  <MenuItem key={major} value={major}>{major}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          {/* Add more form fields for minor, specialization, etc. */}

          <Grid item xs={12}>
            <Typography gutterBottom>
              Core Courses: {formData.coreCount}
            </Typography>
            <Slider
              value={formData.coreCount}
              onChange={handleSliderChange('coreCount')}
              step={1}
              marks
              min={0}
              max={8}
              valueLabelDisplay="auto"
            />
          </Grid>

          <Grid item xs={12}>
            <Typography gutterBottom>
              Elective Courses: {formData.electiveCount}
            </Typography>
            <Slider
              value={formData.electiveCount}
              onChange={handleSliderChange('electiveCount')}
              step={1}
              marks
              min={0}
              max={8}
              valueLabelDisplay="auto"
            />
          </Grid>

          {totalCourses > 8 && (
            <Grid item xs={12}>
              <Alert severity="warning">
                Total number of courses cannot exceed 8
              </Alert>
            </Grid>
          )}

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Degree Type</InputLabel>
              <Select
                name="degreeType"
                value={formData.degreeType}
                onChange={handleChange}
                label="Degree Type"
              >
                <MenuItem value="Please Select an Option">Please Select an Option</MenuItem>
                <MenuItem value="Undergraduate">Undergraduate</MenuItem>
                <MenuItem value="Master">Master</MenuItem>
                <MenuItem value="PHD">PHD</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Have you completed any courses at UVic or have transfer credits?</InputLabel>
              <Select
                name="studentStatus"
                value={formData.studentStatus}
                onChange={handleChange}
                label="Have you completed any courses at UVic or have transfer credits?"
              >
                <MenuItem value="Please Select an Option">Please Select an Option</MenuItem>
                <MenuItem value="Yes">Yes</MenuItem>
                <MenuItem value="No">No</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          {error && (
            <Grid item xs={12}>
              <Alert severity="error">{error}</Alert>
            </Grid>
          )}

          <Grid item xs={12}>
            <Button 
              variant="contained" 
              color="primary" 
              size="large"
              onClick={handleSubmit}
              disabled={totalCourses > 8}
            >
              Next
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
}

export default UserPreferences;
