import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container, Box, Typography, TextField, Button,
  FormControl, InputLabel, Select, MenuItem,
  Slider, Grid, Alert
} from '@mui/material';
import axios from 'axios';

function UserPreferences() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    name: '',
    major: 'Select a Major',
    minor: '',
    specialization: '',
    coreCount: 4,
    electiveCount: 2,
    degreeType: 'Please Select an Option',
    studentStatus: 'Please Select an Option',
    year: 'Please Select an Option',
    supportsCoop: 'Please Select an Option',
    coopCompleted: 0,
    coopPlanned: 0
  });

  const [majors, setMajors] = useState([]);
  const [totalCourses, setTotalCourses] = useState(6);
  const [error, setError] = useState('');

  // Fetch majors from backend and check if backend is running
  useEffect(() => {
    const checkBackendAndFetchMajors = async () => {
      try {
        // First check if backend is running
        await axios.get('http://127.0.0.1:8000/');
        console.log('Backend is running');

        // Then fetch majors
        try {
          const response = await axios.get('http://127.0.0.1:8000/majors');
          console.log('Majors fetched:', response.data);
          setMajors(['Select a Major', ...response.data]);
        } catch (error) {
          console.error('Error fetching majors:', error);
          setMajors([
            'Select a Major',
            'Software Engineering',
            'Computer Science',
            'Electrical Engineering',
            'Computer Engineering',
            'Biomedical Engineering',
            'Mechanical Engineering',
            'Civil Engineering'
          ]);
        }
      } catch (error) {
        console.error('Backend is not running:', error);
        setError('Cannot connect to the backend server. Please make sure it is running.');
        setMajors([
          'Select a Major',
          'Software Engineering',
          'Computer Science',
          'Electrical Engineering',
          'Computer Engineering',
          'Biomedical Engineering',
          'Mechanical Engineering',
          'Civil Engineering'
        ]);
      }
    };

    checkBackendAndFetchMajors();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    console.log(`Changing ${name} to ${value}`);

    // Handle numeric inputs
    if (name === 'coopCompleted' || name === 'coopPlanned') {
      setFormData(prev => ({ ...prev, [name]: parseInt(value) || 0 }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSliderChange = (name) => (_, newValue) => {
    setFormData(prev => ({ ...prev, [name]: newValue }));

    if (name === 'coreCount' || name === 'electiveCount') {
      const core = name === 'coreCount' ? newValue : formData.coreCount;
      const elective = name === 'electiveCount' ? newValue : formData.electiveCount;
      setTotalCourses(core + elective);
    }
  };

  const handleSubmit = async () => {
    console.log('Submit button clicked');
    console.log('Form data:', formData);

    if (formData.major === 'Select a Major') {
      setError('Please select a major to continue.');
      return;
    }

    try {
      // Prepare all user preferences to send to the backend
      const userData = {
        major: formData.major,
        name: formData.name,
        minor: formData.minor,
        specialization: formData.specialization,
        number_of_courses: formData.coreCount + formData.electiveCount,
        core_courses: formData.coreCount,
        elective_courses: formData.electiveCount,
        degree_type: formData.degreeType,
        student_status: formData.studentStatus,
        year: formData.year,
        supports_coop: formData.supportsCoop,
        coop_completed: formData.coopCompleted,
        coop_planned: formData.coopPlanned
      };

      console.log('Sending data to backend:', userData);

      // Send user preferences to backend
      const response = await axios.post('http://127.0.0.1:8000/extract_courses', userData);
      console.log('Backend response:', response.data);

      // Store the course list in localStorage
      if (Array.isArray(response.data)) {
        localStorage.setItem('courseList', JSON.stringify(response.data));
        console.log('Course list stored in localStorage:', response.data);

        // Also store the course list in the backend
        try {
          await axios.post('http://127.0.0.1:8000/course_list', {
            courses: response.data
          });
          console.log('Course list sent to backend');
        } catch (error) {
          console.error('Error sending course list to backend:', error);
        }
      } else {
        console.warn('Backend did not return an array of courses:', response.data);
      }

      // Navigate based on student status
      if (formData.studentStatus === 'Yes') {
        console.log('Navigating to course-list');
        navigate('/course-list');
      } else if (formData.studentStatus === 'No') {
        console.log('Navigating to chatbot');
        navigate('/chatbot');
      } else {
        console.log('Student status not selected:', formData.studentStatus);
      }
    } catch (error) {
      console.error('Error submitting preferences:', error);
      setError('Failed to submit preferences. Please try again.');
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Your Academic Profile
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Your Name"
              name="name"
              value={formData.name}
              onChange={handleChange}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Major</InputLabel>
              <Select
                name="major"
                value={formData.major}
                onChange={handleChange}
                label="Major"
              >
                {majors.map(major => (
                  <MenuItem key={major} value={major}>{major}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Minor (optional)"
              name="minor"
              value={formData.minor}
              onChange={handleChange}
              placeholder="e.g., Business, Biology"
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <TextField
              fullWidth
              label="Specialization (optional)"
              name="specialization"
              value={formData.specialization}
              onChange={handleChange}
              placeholder="e.g., AI, Cybersecurity"
            />
          </Grid>

          <Grid item xs={12}>
            <Typography gutterBottom>
              Core Courses: {formData.coreCount}
            </Typography>
            <Slider
              value={formData.coreCount}
              onChange={handleSliderChange('coreCount')}
              step={1}
              marks
              min={0}
              max={8}
              valueLabelDisplay="auto"
            />
          </Grid>

          <Grid item xs={12}>
            <Typography gutterBottom>
              Elective Courses: {formData.electiveCount}
            </Typography>
            <Slider
              value={formData.electiveCount}
              onChange={handleSliderChange('electiveCount')}
              step={1}
              marks
              min={0}
              max={8}
              valueLabelDisplay="auto"
            />
          </Grid>

          {totalCourses > 8 && (
            <Grid item xs={12}>
              <Alert severity="warning">
                Total number of courses cannot exceed 8
              </Alert>
            </Grid>
          )}

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Degree Type</InputLabel>
              <Select
                name="degreeType"
                value={formData.degreeType}
                onChange={handleChange}
                label="Degree Type"
              >
                <MenuItem value="Please Select an Option">Please Select an Option</MenuItem>
                <MenuItem value="Undergraduate">Undergraduate</MenuItem>
                <MenuItem value="Master">Master</MenuItem>
                <MenuItem value="PHD">PHD</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Have you completed any courses at UVic or have transfer credits?</InputLabel>
              <Select
                name="studentStatus"
                value={formData.studentStatus}
                onChange={handleChange}
                label="Have you completed any courses at UVic or have transfer credits?"
              >
                <MenuItem value="Please Select an Option">Please Select an Option</MenuItem>
                <MenuItem value="Yes">Yes</MenuItem>
                <MenuItem value="No">No</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>What year are you in?</InputLabel>
              <Select
                name="year"
                value={formData.year}
                onChange={handleChange}
                label="What year are you in?"
              >
                <MenuItem value="Please Select an Option">Please Select an Option</MenuItem>
                <MenuItem value="Year 1">Year 1</MenuItem>
                <MenuItem value="Year 2">Year 2</MenuItem>
                <MenuItem value="Year 3">Year 3</MenuItem>
                <MenuItem value="Year 4">Year 4</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          {(formData.year === 'Year 2' || formData.year === 'Year 3' || formData.year === 'Year 4') && (
            <Grid item xs={12} md={6}>
              <FormControl fullWidth>
                <InputLabel>Does your program support co-op?</InputLabel>
                <Select
                  name="supportsCoop"
                  value={formData.supportsCoop}
                  onChange={handleChange}
                  label="Does your program support co-op?"
                >
                  <MenuItem value="Please Select an Option">Please Select an Option</MenuItem>
                  <MenuItem value="Yes">Yes</MenuItem>
                  <MenuItem value="No">No</MenuItem>
                </Select>
              </FormControl>
            </Grid>
          )}

          {formData.supportsCoop === 'Yes' && (
            <>
              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="How many co-op terms have you already completed?"
                  name="coopCompleted"
                  type="number"
                  InputProps={{ inputProps: { min: 0, max: 4 } }}
                  value={formData.coopCompleted}
                  onChange={handleChange}
                />
              </Grid>

              <Grid item xs={12} md={6}>
                <TextField
                  fullWidth
                  label="How many total co-op terms would you like to finish by graduation?"
                  name="coopPlanned"
                  type="number"
                  InputProps={{ inputProps: { min: 0, max: 4 } }}
                  value={formData.coopPlanned}
                  onChange={handleChange}
                />
              </Grid>
            </>
          )}

          {error && (
            <Grid item xs={12}>
              <Alert severity="error">{error}</Alert>
            </Grid>
          )}

          <Grid item xs={12}>
            <Button
              variant="contained"
              color="primary"
              size="large"
              onClick={handleSubmit}
              disabled={totalCourses > 8}
            >
              Next
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
}

export default UserPreferences;
