import React, { useState, useEffect, useRef } from 'react';
import {
  Container, Box, Typography, TextField, Button,
  Paper, List, ListItem, Avatar, CircularProgress
} from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import PersonIcon from '@mui/icons-material/Person';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import axios from 'axios';

function Chatbot() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);

  // Scroll to bottom of messages
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Initial greeting from assistant and fetch completed courses
  useEffect(() => {
    const fetchCompletedCourses = async () => {
      try {
        console.log('Fetching completed courses...');
        const response = await axios.get('http://127.0.0.1:8000/courses_completed');
        console.log('Completed courses:', response.data);

        let completedCoursesText = '';

        if (Array.isArray(response.data) && response.data.length > 0) {
          completedCoursesText = `\n\nI see you've completed these courses: ${response.data.join(', ')}`;
        } else if (typeof response.data === 'string' && response.data.trim()) {
          completedCoursesText = `\n\nI see you've completed these courses: ${response.data}`;
        }

        setMessages([
          {
            role: 'assistant',
            content: `Hello! I'm your UVic course planning assistant. How can I help you today?${completedCoursesText}`
          }
        ]);
      } catch (error) {
        console.error('Error fetching completed courses:', error);
        setMessages([
          {
            role: 'assistant',
            content: 'Hello! I\'m your UVic course planning assistant. How can I help you today?'
          }
        ]);
      }
    };

    fetchCompletedCourses();
  }, []);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    const userInput = input;  // Save the input before clearing it
    setInput('');
    setLoading(true);

    try {
      console.log('Sending message to backend:', userInput);

      // Send message to backend
      const response = await axios.post('http://127.0.0.1:8000/chat', {
        message: userInput
      });

      console.log('Backend response:', response.data);

      const assistantMessage = {
        role: 'assistant',
        content: response.data.response || 'I received your message but I\'m not sure how to respond.'
      };

      setMessages(prev => [...prev, assistantMessage]);
    } catch (error) {
      console.error('Error sending message:', error);

      let errorMessage = 'Sorry, I encountered an error. Please try again.';

      if (error.response) {
        // The request was made and the server responded with a status code
        // that falls out of the range of 2xx
        console.error('Error response data:', error.response.data);
        console.error('Error response status:', error.response.status);

        if (error.response.status === 404) {
          errorMessage = 'Sorry, the chat service is not available right now. Please try again later.';
        } else if (error.response.status === 500) {
          errorMessage = 'Sorry, there was a server error. Please try again later.';
        }
      } else if (error.request) {
        // The request was made but no response was received
        console.error('Error request:', error.request);
        errorMessage = 'Sorry, I couldn\'t connect to the server. Please check your internet connection.';
      }

      setMessages(prev => [
        ...prev,
        {
          role: 'assistant',
          content: errorMessage
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4, display: 'flex', flexDirection: 'column', height: '80vh' }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Course Planning Assistant
        </Typography>

        <Paper
          elevation={3}
          sx={{
            p: 2,
            mb: 2,
            flexGrow: 1,
            overflow: 'auto',
            bgcolor: '#f5f5f5'
          }}
        >
          <List>
            {messages.map((message, index) => (
              <ListItem
                key={index}
                sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: message.role === 'user' ? 'flex-end' : 'flex-start',
                  mb: 2
                }}
              >
                <Box
                  sx={{
                    display: 'flex',
                    maxWidth: '80%',
                    bgcolor: message.role === 'user' ? 'primary.light' : 'white',
                    color: message.role === 'user' ? 'white' : 'text.primary',
                    borderRadius: 2,
                    p: 2,
                    boxShadow: 1
                  }}
                >
                  <Avatar
                    sx={{
                      mr: 1,
                      bgcolor: message.role === 'user' ? 'primary.main' : 'secondary.main'
                    }}
                  >
                    {message.role === 'user' ? <PersonIcon /> : <SmartToyIcon />}
                  </Avatar>
                  <Typography>{message.content}</Typography>
                </Box>
              </ListItem>
            ))}
            <div ref={messagesEndRef} />
          </List>
          {loading && (
            <Box sx={{ display: 'flex', justifyContent: 'center', my: 2 }}>
              <CircularProgress size={24} />
            </Box>
          )}
        </Paper>

        <Box sx={{ display: 'flex' }}>
          <TextField
            fullWidth
            variant="outlined"
            placeholder="Type your message here..."
            value={input}
            onChange={handleInputChange}
            onKeyDown={handleKeyDown}
            disabled={loading}
            multiline
            maxRows={4}
          />
          <Button
            variant="contained"
            color="primary"
            endIcon={<SendIcon />}
            onClick={handleSendMessage}
            disabled={!input.trim() || loading}
            sx={{ ml: 1 }}
          >
            Send
          </Button>
        </Box>
      </Box>
    </Container>
  );
}

export default Chatbot;
